import React from 'react';

const MetricsToReconsider = ({ metrics }) => {
  return (
    <div className="bg-gradient-to-br from-white to-red-50 p-6 rounded-lg shadow-lg border border-red-200">
      <h2 className="text-xl font-bold text-red-600 mb-4 border-b-2 border-red-300 pb-2">Metrics to Reconsider</h2>
      <p className="text-gray-700 mb-4 font-medium">These metrics may be misleading or not driving actual outcomes.</p>

      <div className="mt-6">
        {metrics.map((metric, index) => (
          <div key={index} className="mb-4 p-3 bg-red-100 rounded-lg border-l-4 border-red-500">
            <h3 className="font-bold text-gray-800">{index + 1}. {metric.name}</h3>
            <p className="text-sm text-gray-600">Issue: {metric.reason}</p>
            <div className="mt-2 flex gap-2 flex-wrap">
              <span className="px-2 py-1 bg-red-500 text-white text-xs rounded-full">Low impact</span>
              {metric.reason && metric.reason.includes('vanity') && (
                <span className="px-2 py-1 bg-orange-500 text-white text-xs rounded-full">Vanity metric</span>
              )}
              {metric.reason && metric.reason.includes('not used') && (
                <span className="px-2 py-1 bg-purple-500 text-white text-xs rounded-full">Not actionable</span>
              )}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 p-4 bg-gradient-to-r from-yellow-100 to-amber-100 rounded-lg border-l-4 border-yellow-500 shadow-md">
        <h3 className="font-bold text-yellow-800 mb-2">Improvement Recommendations</h3>
        <ul className="list-disc pl-5 text-gray-800 space-y-2">
          <li>Remove these metrics from dashboards to reduce noise</li>
          <li>Replace with recommended metrics on the left</li>
          <li>Set up regular metric reviews (quarterly minimum)</li>
          <li>Ensure each metric has a clear owner and decision path</li>
        </ul>
      </div>
    </div>
  );
};

export default MetricsToReconsider;
