import React from 'react';

const DepartmentSelector = ({ departments, selectedDepartment, onSelectDepartment }) => {
  return (
    <div className="mb-6">
      <h2 className="text-xl font-bold text-indigo-800 mb-3">Select a Department:</h2>
      <div className="flex flex-wrap gap-2">
        {departments.map(dept => (
          <button
            key={dept}
            className={`px-4 py-2 rounded-lg transition-colors shadow-md ${
              selectedDepartment === dept 
                ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-bold' 
                : 'bg-gray-200 text-gray-800 hover:bg-indigo-100 hover:text-indigo-800'
            }`}
            onClick={() => onSelectDepartment(dept)}
          >
            {dept}
          </button>
        ))}
      </div>
    </div>
  );
};

export default DepartmentSelector;
