import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const RecommendedMetrics = ({ metrics }) => {
  return (
    <div className="bg-gradient-to-br from-white to-green-50 p-6 rounded-lg shadow-lg border border-green-200">
      <h2 className="text-xl font-bold text-green-600 mb-4 border-b-2 border-green-300 pb-2">Recommended Metrics</h2>
      <p className="text-gray-700 mb-4 font-medium">These metrics show strong correlation with business outcomes and decision-making.</p>

      <div className="h-64">
        <ResponsiveContainer width="100%" height="100%">
          <BarChart
            data={metrics}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis label={{ value: 'Value Score', angle: -90, position: 'insideLeft' }} />
            <Tooltip />
            <Bar dataKey="score" fill="#10B981" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="mt-6">
        {metrics.map((metric, index) => (
          <div key={index} className="mb-4 p-3 bg-green-100 rounded-lg border-l-4 border-green-500">
            <h3 className="font-bold text-gray-800">{index + 1}. {metric.name}</h3>
            <p className="text-sm text-gray-600">Value score: {metric.score}</p>
            <div className="mt-2 flex gap-2 flex-wrap">
              <span className="px-2 py-1 bg-green-500 text-white text-xs rounded-full">Business impact</span>
              <span className="px-2 py-1 bg-blue-500 text-white text-xs rounded-full">Decision driver</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedMetrics;
